from django.db import modelsfrom django.contrib.auth.models import AbstractBaseUser, PermissionsMixinclass User(AbstractBaseUser, PermissionsMixin):    name = models.CharField()    phone_number = models.CharField(unique=True)    is_active = models.BooleanField(verbose_name="Актив", default=True)    is_staff = models.BooleanField(verbose_name='Админ', default=False)    USERNAME_FIELD = 'phone_number'    class Meta:        verbose_name = 'Пользователь'        verbose_name_plural = 'Пользователи'