from django.db import modelsfrom django.contrib.auth.models import AbstractUser, PermissionsMixinfrom apps.movies.models import Moviesclass User(AbstractUser, PermissionsMixin):    email = models.EmailField(unique=True)    viewed = models.ManyToManyField(Movies)    is_active = models.BooleanField(verbose_name="Актив", default=True)    is_staff = models.BooleanField(verbose_name='Админ', default=False)    USERNAME_FIELD = 'email'    REQUIRED_FIELDS = ['username']    class Meta:        verbose_name = 'Пользователь'        verbose_name_plural = 'Пользователи'